mapper.update:
UPDATE exam
exam_year = #{examYear:VARCHAR}, exam_semester = #{examSemester:VARCHAR} , exam_term = #{examTerm:VARCHAR}, exam_subject = #{examSubject:VARCHAR}, exam_score = #{examScore:VARCHAR}
WHERE exam_code = #{examCode}

mapper.select:
SELECT * 
FROM (
	    SELECT inner_table.*, ROWNUM AS row_seq
	    FROM ( SELECT * FROM exam 
				WHERE student_id = #{searchId} AND exam_subject = #{searchKeyword}
				ORDER BY exam_subject
		)inner_table 
    WHERE ROWNUM <= #{endRowNum} ) inner_table
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * 
FROM (
	    SELECT inner_table.*, ROWNUM AS row_seq
	    FROM ( SELECT * FROM exam 
				WHERE student_id = #{searchId}
				ORDER BY exam_subject
		)inner_table 
    WHERE ROWNUM <= #{endRowNum} ) inner_table
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
