mapper.update:
UPDATE lesson
lesson_name = #{lessonName:VARCHAR}, lesson_day = #{lessonDay:VARCHAR}, lesson_start_time = #{lessonStartTime:VARCHAR}, lesson_end_time = #{lessonEndTime:VARCHAR}, lesson_place = #{lessonPlace:VARCHAR}, fees = #{fees:NUMERIC}, subject = #{subject:VARCHAR}, lesson_content = #{lessonContent:VARCHAR}
WHERE lesson_code = #{lessonCode}

mapper.select:
SELECT* FROM (SELECT inner_table.*, ROWNUM AS row_seq FROM (SELECT DISTINCT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time,L.lesson_place, L.subject, L.lesson_code, SR.proposal, L.lesson_create_at, SR.student_name FROM LESSON L, USERS U, students_record SR WHERE L.teacher_id = U.user_id AND teacher_id = #{searchId}
AND L.lesson_name like '%${searchKeyword}%'
ORDER BY L.lesson_create_at desc) inner_table WHERE ROWnUM <= #{endRowNum}) inner_table WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}

SELECT * FROM (SELECT inner_table.*, ROWNUM AS row_seq FROM( SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time, L.lesson_place, L.subject, L.lesson_code, SR.proposal, L.teacher_Id, SR.student_name FROM LESSON L, STUDENTS_RECORD SR, USERS U WHERE L.lesson_code = SR.lesson_code and L.teacher_id = U.user_id and SR.student_id = #{searchId}
AND L.lesson_name like '%${searchKeyword}%'
AND U.user_name like '%${searchKeyword}%'
ORDER BY L.lesson_create_at desc)inner_table WHERE ROWNUM < = #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}

SELECT L.*, B.student_id, B.student_name, B.user_name, B.proposal FROM LESSON L, (SELECT SR.lesson_code SRL, SR.student_id student_id, SR.student_name student_name, A.UU user_name,SR.proposal proposal FROM students_record SR, (SELECT student_id SI, U.user_name UU FROM users U, relation R WHERE U.user_id = R.parent_id AND U.user_id = #{searchId}) A WHERE SR.student_id = A.SI
AND SR.student_name like '%${searchKeyword}%'
) B WHERE L.lesson_code = B.SRL

select L.*
from lesson L, students_record SR
where L.lesson_code = SR.lesson_code and SR.student_id = #{searchId}

 		SELECT COUNT(*)
 		FROM( SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time,L.lesson_place, L.subject, L.lesson_code
			FROM LESSON L, USERS U 
			WHERE L.teacher_id = U.user_id  AND teacher_id = #{searchId}


 					AND lesson_name like '%${searchKeyword}%'

 		 ) countTable

SELECT COUNT(*) FROM( SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time, L.lesson_place, L.subject, L.lesson_code, SR.proposal, L.teacher_Id FROM LESSON L, STUDENTS_RECORD SR, USERS U WHERE L.lesson_code = SR.lesson_code and L.teacher_id = U.user_id and SR.student_id = #{searchId}
AND L.lesson_name like '%${searchKeyword}%'
AND U.user_name like '%${searchKeyword}%'
) countTable

select *
from (select inner_table.*, ROWNUM AS row_seq
	FROM (SELECT B.isbn, B.book_Img, B.book_title, B.author, B.publisher, B.book_year, B.book_price, L.lesson_name, L.lesson_code
		FROM BOOK B, LESSON_BOOK LB, LESSON L
		WHERE B.isbn=LB.isbn AND LB.lesson_code = L.lesson_code and	L.teacher_id = #{searchId}


				AND L.lesson_name like '%${searchKeyword}%'

		 ) inner_table
		WHERE ROWNUM < = #{endRowNum})
WHERE ROW_SEQ BETWEEN #{startRowNum} AND #{endRowNum}

 		SELECT COUNT(*)
 		FROM(SELECT B.isbn, B.book_Img, B.book_title, B.author, B.publisher, B.book_year, B.book_price, L.lesson_name, L.lesson_code
			FROM BOOK B, LESSON_BOOK LB, LESSON L
			WHERE B.isbn=LB.isbn AND LB.lesson_code = L.lesson_code and	L.teacher_id = #{searchId}


 					AND L.lesson_name like '%${searchKeyword}%'

 		 	) countTable
