mapper.update:
UPDATE relation

	 student_id = #{firstStudentId} , 
	 parentId = #{parentId} , 
	relation_name = #{relationName}

WHERE relation_code = #{relationCode}

mapper.select:
SELECT *
FROM (SELECT inner_table.*, ROWNUM AS row_seq
FROM ( SELECT * FROM relation
WHERE student_id = #{searchId}



			parent_id = #{searchKeyword}




)inner_table
WHERE ROWNUM < = #{endRowNum})
WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}

SELECT *
FROM (SELECT inner_table.*, ROWNUM AS row_seq
FROM ( SELECT * FROM relation
WHERE parent_id = #{searchId}

	student_id = #{searchKeyword}

)inner_table
WHERE ROWNUM < = #{endRowNum})
WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}

SELECT COUNT(*)
FROM (SELECT * FROM relation
WHERE student_id = #{searchId}



			parent_id = #{searchKeyword}


 ) countTable

SELECT COUNT(*)
FROM (SELECT * FROM relation
WHERE parent_id = #{searchId}

	student_id = #{searchKeyword}
) countTable

SELECT *
FROM (SELECT inner_table.*, ROWNUM AS row_seq
FROM (SELECT u.user_name
		FROM RELATION R, USERS U
		WHERE U.user_Id = R.student_id and R.parent_id = #{parentId}

			student_id = #{searchKeyword}

)inner_table
WHERE ROWNUM < = #{endRowNum})
WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}
