mapper.update:
UPDATE students_record

	fees = #{fees},
	pay_due_date = #{payDueDate}	

WHERE record_code = #{recordCode}

UPDATE students_record

	proposal = #{proposal: CHAR}

WHERE record_code = #{recordCode}

mapper.select:
SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT * FROM ( SELECT sr.record_code, sr.lesson_code, sr.lesson_name, sr.student_id, sr.student_name, sr.proposal, sr.lesson_start_date, sr.fees, sr.pay_due_date FROM students_record sr, lesson l WHERE l.teacher_id = #{searchId} AND sr.lesson_code = l.lesson_code) v WHERE v.proposal = '1'
AND v.student_id LIKE '%' || #{searchKeyword} || '%'
AND v.student_name LIKE '%' || #{searchKeyword} || '%'
AND v.lesson_code LIKE #{searchKeyword}
AND v.lesson_name LIKE '%' || #{searchKeyword} || '%'
ORDER BY v.student_name ) inner_table WHERE ROWNUM <= #{endRowNum}) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
